","        $('#message').val(\"\");","        e.preventDefault(); // 브라우져의 기본 이벤트를 해제 시킨다.","        // submit의 기능으로 다음페이지로 넘어가는 기본이벤트를 해제시킴으로써 서버에 단순히 전송만 하게된다.","      });","      // $('#chat') 부분부터 여기까지는 사용자에게 내가 보낸 메세지와 이름을 보내는 곳","","      socket.on('receive message', function(msg){","        $('#chatLog').append(msg+\"\\n\");","        //$(추가할내용).appendTo(클래스명); 의 형식으로도 사용가능하다.","        $('#chatLog').scrollTop($('#chatLog').prop('scrollHeight'))","        //prop('scrollHeight')은 스크롤의 높이를 구해서 넘겨준다","        //scrollTop() 조건에 일치하는 요소들의 수직 스크롤의 위치를 value값으로 세팅합니다.","      });","      socket.on('change name', function(name){","        $('#name').val(name);","      });","      socket.on('nameSaver', function(name){","        $('#chatLog').append(name+\"님이 입장하셧습니다\\n\");","      });","    </script>"],"id":101}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":39,"column":36},"end":{"row":39,"column":36},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"hash":"7af91c9745f57a9f90604ec70796fd4bc0e446df","timestamp":1501780411204}