{"changed":false,"filter":false,"title":"RouteSignatureParameters.php","tooltip":"/harmony_project/vendor/laravel/framework/src/Illuminate/Routing/RouteSignatureParameters.php","value":"<?php\n\nnamespace Illuminate\\Routing;\n\nuse ReflectionMethod;\nuse ReflectionFunction;\nuse Illuminate\\Support\\Str;\n\nclass RouteSignatureParameters\n{\n    /**\n     * Extract the route action's signature parameters.\n     *\n     * @param  array  $action\n     * @param  string  $subClass\n     * @return array\n     */\n    public static function fromAction(array $action, $subClass = null)\n    {\n        $parameters = is_string($action['uses'])\n                        ? static::fromClassMethodString($action['uses'])\n                        : (new ReflectionFunction($action['uses']))->getParameters();\n\n        return is_null($subClass) ? $parameters : array_filter($parameters, function ($p) use ($subClass) {\n            return $p->getClass() && $p->getClass()->isSubclassOf($subClass);\n        });\n    }\n\n    /**\n     * Get the parameters for the given class / method by string.\n     *\n     * @param  string  $uses\n     * @return array\n     */\n    protected static function fromClassMethodString($uses)\n    {\n        list($class, $method) = Str::parseCallback($uses);\n\n        return (new ReflectionMethod($class, $method))->getParameters();\n    }\n}\n","undoManager":{"mark":-1,"position":-1,"stack":[]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":39,"column":5},"end":{"row":39,"column":5},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1496668511523}